
public class Model {

	private int i;
	private int sum;
	private int average;
	
	public Model(int i) {
		// TODO Auto-generated constructor stub
		this.i = i;
		this.sum = 0;
		this.average = 0;
	}
	
	public int geti() {
		return this.i;
	}
	public int getSum() {
		return this.sum;
	}
	public int getAverage() {
		return this.average;
	}
	public void setSum(int sum) {
		this.sum = sum;
	}
	public void setAverage(int average) {
		this.average = average;
	}

	
}




